hudson.tasks.Shell.command:
rm -rf ${B3_HOME}
mkdir -p ${B3_HOME}
cd ${B3_HOME}/../
if [ ! -f director/director ];
then
  wget http://download.eclipse.org/tools/buckminster/products/director_latest.zip
  unzip director_latest.zip
fi
if [[ ! -f ${B3_HOME}/b3 ||  ${UPDATE} == 'true' ]];
then
  director/director -vm $JAVA_HOME/bin/java -d ${B3_HOME} -p b3 -r "http://download.eclipse.org/modeling/emft/b3/headless-4.2/" -installIU "org.eclipse.b3.cli.product, org.eclipse.b3.aggregator.engine.feature.feature.group"
fi

rm -fr ${BUCKY_HOME}
mkdir -p ${BUCKY_HOME}
cd ${BUCKY_HOME}/../
if [ ! -f director/director ];
then
  wget http://download.eclipse.org/tools/buckminster/products/director_latest.zip
  unzip director_latest.zip
fi
if [[ ! -f ${BUCKY_HOME}/buckminster ||  ${UPDATE} == 'true' ]];
then
  director/director -vm $JAVA_HOME/bin/java -d ${BUCKY_HOME} -p Buckminster -r "http://download.eclipse.org/tools/buckminster/headless-4.4/, http://download.eclipse.org/eclipse/updates/4.4" -installIU "org.eclipse.equinox.p2.director.feature.feature.group, org.eclipse.buckminster.pde.headless.feature.feature.group, org.eclipse.buckminster.maven.headless.feature.feature.group, org.eclipse.buckminster.cmdline.product, org.eclipse.buckminster.core.headless.feature.feature.group, org.eclipse.buckminster.git.headless.feature.feature.group, org.eclipse.buckminster.cvs.headless.feature.feature.group, org.eclipse.pde.api.tools.ee.feature.feature.group"
fi
