hudson.tasks.Shell.command:
rm -rf ${BUCKY_MWE2_HOME}
mkdir -p ${BUCKY_MWE2_HOME}
cd ${BUCKY_MWE2_HOME}/../
if [ ! -f director/director ];
then
  wget http://download.eclipse.org/tools/buckminster/products/director_latest.zip
  unzip director_latest.zip
fi
if [[ ! -f ${BUCKY_MWE2_HOME}/buckminster ]];
then
  director/director -vm $JAVA_HOME/bin/java -d ${BUCKY_MWE2_HOME} -p Buckminster -r "http://download.eclipse.org/tools/buckminster/headless-4.4/, http://download.eclipse.org/eclipse/updates/4.4,http://download.eclipse.org/modeling/tmf/xtext/updates/composite/latest" -installIU "org.eclipse.equinox.p2.director.feature.feature.group, org.eclipse.buckminster.pde.headless.feature.feature.group, org.eclipse.buckminster.maven.headless.feature.feature.group, org.eclipse.buckminster.cmdline.product, org.eclipse.buckminster.core.headless.feature.feature.group, org.eclipse.buckminster.git.headless.feature.feature.group, org.eclipse.buckminster.cvs.headless.feature.feature.group, org.eclipse.pde.api.tools.ee.feature.feature.group, org.eclipse.emf.mwe2.launcher.feature.group"
fi
